const request = require('superagent')
const utils = require('./utils')

function getScoresByID(beatmapID) {
	return new Promise((resolve, reject) => {
		request.get(utils.host + '/scores?b=' + beatmapID).set('User-Agent', 'ilytRAPI').end((error, response) => {
			if (!error) {
						resolve(response.body)
					} else {
						reject(new Error(error.status || error.response));
					}
			})
	})
}

module.exports = getScoresByID;