const request = require('superagent')
const utils = require('./utils')

function getScoresByMD5(beatmapMD5) {
	return new Promise((resolve, reject) => {
		request.get(utils.host + '/scores?md5=' + beatmapMD5).set('User-Agent', 'ilytRAPI').end((error, response) => {
			if (!error) {
						resolve(response.body)
					} else {
						reject(new Error(error.status || error.response));
					}
			})
	})
}

module.exports = getScoresByMD5;