const request = require('superagent')
const utils = require('./utils')

function getUserBestScores(name) {
	return new Promise((resolve, reject) => {
		request.get(utils.host + '/users/scores/best?name=' + name).set('User-Agent', 'ilytRAPI').end((error, response) => {
					if (!error) {
						resolve(response.body)
					} else {
						reject(new Error(error.status || error.response));
					}
				})

	})
}
module.exports = getUserBestScores;